              <option value="soft">ðŸ’§ Soft Water (RO/Distilled)</option>
              <option value="hard">ðŸª¨ Hard Water (Tap)</option>
            </select>
          </div>
        </div>
      </div>

      {/* Results */}
      {calculations && (
        <div className="space-y-6">
          {/* Recipe Card */}
          <div className="card">
            <h2 className="section-title">ðŸ“‹ Your Nutrient Recipe</h2>

            <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-6">
