import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Sprout, Droplets, Scissors, Camera, Plus, Calendar, TrendingUp } from 'lucide-react';
import { formatDistanceToNow } from 'date-fns';
import toast from 'react-hot-toast';

import { plantsApi, logsApi } from '../utils/api';

const Dashboard = () => {
  const [plants, setPlants] = useState([]);
  const [recentLogs, setRecentLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    totalPlants: 0,
    activeStages: {},
    recentActivity: 0,
  });

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const [plantsData, logsData] = await Promise.all([
        plantsApi.getAll(),
        logsApi.getAll({ limit: 10 })
      ]);

      setPlants(plantsData);
      setRecentLogs(logsData);

      // Calculate stats
      const stageCount = plantsData.reduce((acc, plant) => {
        acc[plant.stage] = (acc[plant.stage] || 0) + 1;
        return acc;
      }, {});

      setStats({
        totalPlants: plantsData.length,
        activeStages: stageCount,
        recentActivity: logsData.length,
      });
    } catch (error) {
      toast.error('Failed to load dashboard data');
      console.error('Dashboard error:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStageIcon = (stage) => {
    switch (stage) {
      case 'seedling': return 'üå±';
      case 'vegetative': return 'üåø';
      case 'flowering': return 'üå∏';
      case 'harvest': return 'üåæ';
      case 'cured': return 'üì¶';
      default: return 'üå±';
    }
  };

  const getLogIcon = (type) => {
    switch (type) {
      case 'watering': return <Droplets className="w-4 h-4 text-blue-500" />;
      case 'feeding': return <TrendingUp className="w-4 h-4 text-green-500" />;
      case 'pruning': return <Scissors className="w-4 h-4 text-orange-500" />;
      case 'photo': return <Camera className="w-4 h-4 text-purple-500" />;
      default: return <Calendar className="w-4 h-4 text-gray-500" />;
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-64">
        <div className="loading"></div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="dashboard-header">
        <div className="header-content">
          <div className="header-text">
            <h1 className="dashboard-title">üåø Grow Dashboard</h1>
            <p className="dashboard-subtitle">Monitor your cannabis cultivation journey</p>
          </div>
          <div className="header-actions">
            <Link to="/environment" className="btn btn-accent">
              <Camera className="w-4 h-4" />
              Quick Entry
            </Link>
            <Link to="/plants" className="btn btn-primary">
              <Plus className="w-4 h-4" />
              Add Plant
            </Link>
          </div>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="stats-grid">
        <div className="stat-card stat-plants">
          <div className="stat-content">
            <div className="stat-info">
              <p className="stat-label">Active Plants</p>
              <p className="stat-value">{stats.totalPlants}</p>
              <p className="stat-description">Growing strong</p>
            </div>
            <div className="stat-icon">
              <Sprout className="w-8 h-8" />
            </div>
          </div>
          <div className="stat-visual"></div>
        </div>

        <div className="stat-card stat-stages">
          <div className="stat-content">
            <div className="stat-info">
              <p className="stat-label">Growth Stages</p>
              <p className="stat-value">{Object.keys(stats.activeStages).length}</p>
              <p className="stat-description">Different phases</p>
            </div>
            <div className="stat-icon">
              <TrendingUp className="w-8 h-8" />
            </div>
          </div>
          <div className="stat-visual"></div>
        </div>

        <div className="stat-card stat-activity">
          <div className="stat-content">
            <div className="stat-info">
              <p className="stat-label">Recent Logs</p>
              <p className="stat-value">{stats.recentActivity}</p>
              <p className="stat-description">This week</p>
            </div>
            <div className="stat-icon">
              <Calendar className="w-8 h-8" />
            </div>
          </div>
          <div className="stat-visual"></div>
        </div>
      </div>

      {/* Plants Overview */}
      <div className="dashboard-section">
        <div className="section-header">
          <h2 className="section-title">üå± Your Plants</h2>
          <Link to="/plants" className="section-link">
            View All <span className="ml-1">‚Üí</span>
          </Link>
        </div>

        <div className="section-content">
          {plants.length === 0 ? (
            <div className="empty-state">
              <div className="empty-icon">
                <Sprout className="w-16 h-16" />
              </div>
              <h3 className="empty-title">No plants yet</h3>
              <p className="empty-description">Start your cultivation journey by adding your first plant</p>
              <Link to="/plants" className="btn btn-primary">
                <Plus className="w-4 h-4" />
                Add Your First Plant
              </Link>
            </div>
          ) : (
            <div className="plants-grid">
              {plants.slice(0, 4).map((plant) => (
                <Link
                  key={plant.id}
                  to={`/plants/${plant.id}`}
                  className="plant-preview-card"
                >
                  <div className="plant-preview-header">
                    <h3 className="plant-preview-name">{plant.name}</h3>
                    <span className={`stage-badge stage-${plant.stage}`}>
                      {getStageIcon(plant.stage)} {plant.stage}
                    </span>
                  </div>
                  <p className="plant-preview-strain">{plant.strain || 'Unknown strain'}</p>
                  {plant.grow_tent && (
                    <p className="plant-preview-tent">üìç {plant.grow_tent}</p>
                  )}
                  <div className="plant-preview-stats">
                    <span className="plant-stat">
                      üìä {plant.log_count || 0} logs
                    </span>
                    {plant.planted_date && (
                      <span className="plant-stat">
                        üóìÔ∏è {formatDistanceToNow(new Date(plant.planted_date))} ago
                      </span>
                    )}
                  </div>
                </Link>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Recent Activity */}
      <div className="card">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-semibold">Recent Activity</h2>
          <Link to="/logs" className="text-green-600 hover:text-green-700 font-medium">
            View All ‚Üí
          </Link>
        </div>

        {recentLogs.length === 0 ? (
          <div className="text-center py-8">
            <Calendar className="w-12 h-12 text-gray-300 mx-auto mb-4" />
            <p className="text-gray-500">No recent activity</p>
          </div>
        ) : (
          <div className="space-y-3">
            {recentLogs.slice(0, 5).map((log) => (
              <div key={log.id} className="flex items-start gap-3 p-3 bg-gray-50 rounded-lg">
                {getLogIcon(log.type)}
                <div className="flex-1 min-w-0">
                  <div className="flex items-center gap-2 mb-1">
                    <span className="font-medium text-gray-800">{log.plant_name}</span>
                    <span className="text-sm text-gray-500">‚Ä¢</span>
                    <span className="text-sm text-gray-500 capitalize">{log.type}</span>
                  </div>
                  {log.description && (
                    <p className="text-sm text-gray-600">{log.description}</p>
                  )}
                  <p className="text-xs text-gray-500 mt-1">
                    {formatDistanceToNow(new Date(log.logged_at))} ago
                  </p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
