import React, { useState, useEffect } from 'react';
import { Plus, Calendar, Droplets, Scissors, Camera, TrendingUp } from 'lucide-react';
import { useForm } from 'react-hook-form';
import { formatDistanceToNow } from 'date-fns';
import toast from 'react-hot-toast';

import { logsApi, plantsApi } from '../utils/api';

const Logs = () => {
  const [logs, setLogs] = useState([]);
  const [plants, setPlants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);

  const { register, handleSubmit, reset, formState: { errors, isSubmitting } } = useForm();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      const [logsData, plantsData] = await Promise.all([
        logsApi.getAll({ limit: 50 }),
        plantsApi.getAll()
      ]);
      setLogs(logsData);
      setPlants(plantsData);
    } catch (error) {
      toast.error('Failed to load data');
      console.error('Logs fetch error:', error);
    } finally {
      setLoading(false);
    }
  };

  const onSubmit = async (data) => {
    try {
      await logsApi.create(data);
      toast.success('Log added successfully');
      fetchData();
      resetForm();
    } catch (error) {
      toast.error(error.message);
    }
  };

  const resetForm = () => {
    setShowForm(false);
    reset();
  };

  const getLogIcon = (type) => {
    switch (type) {
      case 'watering': return <Droplets className="w-4 h-4 text-blue-500" />;
      case 'feeding': return <TrendingUp className="w-4 h-4 text-green-500" />;
      case 'pruning': return <Scissors className="w-4 h-4 text-orange-500" />;
      case 'photo': return <Camera className="w-4 h-4 text-purple-500" />;
      default: return <Calendar className="w-4 h-4 text-gray-500" />;
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-64">
        <div className="loading"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-800">Activity Logs</h1>
          <p className="text-gray-600 mt-1">Track your plants' activities</p>
        </div>
        <button
          onClick={() => setShowForm(true)}
          className="btn btn-primary"
        >
          <Plus className="w-4 h-4" />
          Add Log
        </button>
      </div>

      {/* Add Log Form */}
      {showForm && (
        <div className="card">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold">Add Activity Log</h2>
            <button onClick={resetForm} className="btn btn-secondary">
              Cancel
            </button>
          </div>

          <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
            <div className="grid grid-2 gap-4">
              <div className="form-group">
                <label className="label">Plant *</label>
                <select 
                  className="select" 
                  {...register('plant_id', { required: 'Plant is required' })}
                >
                  <option value="">Select a plant</option>
                  {plants.map((plant) => (
                    <option key={plant.id} value={plant.id}>
                      {plant.name} ({plant.strain})
                    </option>
                  ))}
                </select>
                {errors.plant_id && <p className="text-error text-sm mt-1">{errors.plant_id.message}</p>}
              </div>

              <div className="form-group">
                <label className="label">Activity Type *</label>
                <select 
                  className="select" 
                  {...register('type', { required: 'Type is required' })}
                >
                  <option value="">Select type</option>
                  <option value="watering">üíß Watering</option>
                  <option value="feeding">üå± Feeding</option>
                  <option value="pruning">‚úÇÔ∏è Pruning</option>
                  <option value="training">ü™¥ Training</option>
                  <option value="observation">üëÅÔ∏è Observation</option>
                  <option value="harvest">üåæ Harvest</option>
                </select>
                {errors.type && <p className="text-error text-sm mt-1">{errors.type.message}</p>}
              </div>

              <div className="form-group">
                <label className="label">Value (optional)</label>
                <input
                  type="number"
                  step="0.1"
                  className="input"
                  {...register('value')}
                  placeholder="e.g., 500 (ml), 6.5 (pH)"
                />
              </div>

              <div className="form-group">
                <label className="label">Unit (optional)</label>
                <input
                  type="text"
                  className="input"
                  {...register('unit')}
                  placeholder="e.g., ml, pH, ppm"
                />
              </div>
            </div>

            <div className="form-group">
              <label className="label">Description</label>
              <textarea
                className="input textarea"
                {...register('description')}
                placeholder="Describe what you did or observed..."
                rows={3}
              />
            </div>

            <div className="flex gap-3">
              <button
                type="submit"
                disabled={isSubmitting}
                className="btn btn-primary"
              >
                {isSubmitting && <div className="loading"></div>}
                Add Log
              </button>
              <button
                type="button"
                onClick={resetForm}
                className="btn btn-secondary"
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Logs List */}
      {logs.length === 0 ? (
        <div className="card text-center py-12">
          <Calendar className="w-16 h-16 text-gray-300 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-600 mb-2">No activity logs yet</h3>
          <p className="text-gray-500 mb-4">Start tracking your plants' activities</p>
          <button
            onClick={() => setShowForm(true)}
            className="btn btn-primary"
          >
            <Plus className="w-4 h-4" />
            Add First Log
          </button>
        </div>
      ) : (
        <div className="space-y-4">
          {logs.map((log) => (
            <div key={log.id} className="card">
              <div className="flex items-start gap-4">
                {getLogIcon(log.type)}
                <div className="flex-1 min-w-0">
                  <div className="flex items-center gap-2 mb-2">
                    <h3 className="font-semibold text-gray-800">{log.plant_name}</h3>
                    <span className="text-sm text-gray-500">‚Ä¢</span>
                    <span className="text-sm font-medium text-gray-700 capitalize">{log.type}</span>
                    <span className="text-sm text-gray-500">‚Ä¢</span>
                    <span className="text-sm text-gray-500">
                      {formatDistanceToNow(new Date(log.logged_at))} ago
                    </span>
                  </div>

                  {log.description && (
                    <p className="text-gray-600 mb-2">{log.description}</p>
                  )}

                  {log.value && (
                    <p className="text-sm text-gray-500">
                      Value: {log.value} {log.unit || ''}
                    </p>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Logs;
