import React, { useState, useEffect } from 'react';
import { Calculator, Beaker, Droplets, TrendingUp, Info, Copy, CheckCircle } from 'lucide-react';
import toast from 'react-hot-toast';

const NutrientCalculator = () => {
  const [selectedBrand, setSelectedBrand] = useState('general-hydroponics');
  const [growthStage, setGrowthStage] = useState('vegetative');
  const [tankSize, setTankSize] = useState(50);
  const [waterType, setWaterType] = useState('soft');
  const [growMedium, setGrowMedium] = useState('hydro');
  const [feedingStrength, setFeedingStrength] = useState('medium');
  const [calculations, setCalculations] = useState(null);
  const [copiedToClipboard, setCopiedToClipboard] = useState(false);

  // Nutrient brand data based on research
  const nutrientBrands = {
    'general-hydroponics': {
      name: 'General Hydroponics FloraSeries',
      description: 'The O.G. 3-Part Hydroponic-Based Nutrient System',
      products: {
        vegetative: [
          { name: 'FloraMicro', ratio: 2.0, unit: 'ml/L' },
          { name: 'FloraGro', ratio: 3.0, unit: 'ml/L' },
          { name: 'FloraBloom', ratio: 1.0, unit: 'ml/L' }
        ],
        flowering: [
          { name: 'FloraMicro', ratio: 2.0, unit: 'ml/L' },
          { name: 'FloraGro', ratio: 1.0, unit: 'ml/L' },
          { name: 'FloraBloom', ratio: 3.0, unit: 'ml/L' }
        ],
        supplements: [
          { name: 'CaliMagic', ratio: 1.0, unit: 'ml/L', optional: true },
          { name: 'Armor Si', ratio: 0.5, unit: 'ml/L', optional: true },
          { name: 'Hydroguard', ratio: 2.0, unit: 'ml/L', hydroOnly: true }
        ]
      },
      strengthMultipliers: {
        light: 0.5,
        medium: 0.75,
        aggressive: 1.0
      }
    },
    'advanced-nutrients': {
      name: 'Advanced Nutrients pH Perfect',
      description: 'pH Perfect Technology for Cannabis - Micro/Grow/Bloom',
      products: {
        vegetative: [
          { name: 'pH Perfect Micro', ratio: 4.0, unit: 'ml/L' },
          { name: 'pH Perfect Grow', ratio: 4.0, unit: 'ml/L' },
          { name: 'pH Perfect Bloom', ratio: 4.0, unit: 'ml/L' }
        ],
        flowering: [
          { name: 'pH Perfect Micro', ratio: 4.0, unit: 'ml/L' },
          { name: 'pH Perfect Grow', ratio: 4.0, unit: 'ml/L' },
          { name: 'pH Perfect Bloom', ratio: 4.0, unit: 'ml/L' }
        ],
        supplements: [
          { name: 'Voodoo Juice', ratio: 2.0, unit: 'ml/L', optional: true },
          { name: 'Big Bud', ratio: 2.0, unit: 'ml/L', optional: true, floweringOnly: true },
          { name: 'Overdrive', ratio: 2.0, unit: 'ml/L', optional: true, floweringOnly: true }
        ]
      },
      strengthMultipliers: {
        light: 0.5,
        medium: 0.75,
        aggressive: 1.0
      }
    },
    'fox-farm': {
      name: 'Fox Farm Trio',
      description: 'Natural & Organic Based Plant Food',
      products: {
        vegetative: [
          { name: 'Grow Big', ratio: 1.0, unit: 'tsp/gal' },
          { name: 'Big Bloom', ratio: 2.0, unit: 'tsp/gal' }
        ],
        flowering: [
          { name: 'Tiger Bloom', ratio: 1.0, unit: 'tsp/gal' },
          { name: 'Big Bloom', ratio: 2.0, unit: 'tsp/gal' }
        ],
        supplements: [
          { name: 'Cal-Mag', ratio: 1.0, unit: 'tsp/gal', optional: true }
        ]
      },
