import React, { useState, useEffect } from 'react';
import { Calculator, Beaker, Droplets, TrendingUp, Info, Copy, CheckCircle } from 'lucide-react';
import toast from 'react-hot-toast';

const NutrientCalculator = () => {
  const [selectedBrand, setSelectedBrand] = useState('general-hydroponics');
  const [growthStage, setGrowthStage] = useState('vegetative');
  const [tankSize, setTankSize] = useState(50);
  const [waterType, setWaterType] = useState('soft');
  const [growMedium, setGrowMedium] = useState('hydro');
  const [feedingStrength, setFeedingStrength] = useState('medium');
  const [calculations, setCalculations] = useState(null);
  const [copiedToClipboard, setCopiedToClipboard] = useState(false);

  // Nutrient brand data based on research
  const nutrientBrands = {
    'general-hydroponics': {
      name: 'General Hydroponics FloraSeries',
      description: 'The O.G. 3-Part Hydroponic-Based Nutrient System',
      products: {
        vegetative: [
          { name: 'FloraMicro', ratio: 2.0, unit: 'ml/L' },
          { name: 'FloraGro', ratio: 2.0, unit: 'ml/L' },
          { name: 'FloraBloom', ratio: 1.0, unit: 'ml/L' }
        ],
        flowering: [
          { name: 'FloraMicro', ratio: 2.0, unit: 'ml/L' },
          { name: 'FloraGro', ratio: 1.0, unit: 'ml/L' },
          { name: 'FloraBloom', ratio: 3.0, unit: 'ml/L' }
        ],
        supplements: [
          { name: 'CaliMagic', ratio: 1.0, unit: 'ml/L', optional: true },
          { name: 'Armor Si', ratio: 0.5, unit: 'ml/L', optional: true },
          { name: 'Hydroguard', ratio: 1.0, unit: 'ml/L', hydroOnly: true }
        ]
      },
      strengthMultipliers: {
        light: 0.5,
        medium: 0.75,
        aggressive: 1.0
      }
    },
    'advanced-nutrients': {
      name: 'Advanced Nutrients pH Perfect',
      description: 'pH Perfect Technology for Cannabis',
      products: {
        vegetative: [
          { name: 'pH Perfect Sensi Grow A', ratio: 2.0, unit: 'ml/L' },
          { name: 'pH Perfect Sensi Grow B', ratio: 2.0, unit: 'ml/L' }
        ],
        flowering: [
          { name: 'pH Perfect Sensi Bloom A', ratio: 2.0, unit: 'ml/L' },
          { name: 'pH Perfect Sensi Bloom B', ratio: 2.0, unit: 'ml/L' }
        ],
        supplements: [
          { name: 'Voodoo Juice', ratio: 2.0, unit: 'ml/L', optional: true },
          { name: 'Big Bud', ratio: 2.0, unit: 'ml/L', optional: true, floweringOnly: true },
          { name: 'Overdrive', ratio: 2.0, unit: 'ml/L', optional: true, floweringOnly: true }
        ]
      },
      strengthMultipliers: {
        light: 0.5,
        medium: 0.75,
        aggressive: 1.0
      }
    },
    'fox-farm': {
      name: 'Fox Farm Trio',
      description: 'Natural & Organic Based Plant Food',
      products: {
        vegetative: [
          { name: 'Grow Big', ratio: 1.0, unit: 'tsp/gal' },
          { name: 'Big Bloom', ratio: 2.0, unit: 'tsp/gal' }
        ],
        flowering: [
          { name: 'Tiger Bloom', ratio: 1.0, unit: 'tsp/gal' },
          { name: 'Big Bloom', ratio: 2.0, unit: 'tsp/gal' }
        ],
        supplements: [
          { name: 'Cal-Mag', ratio: 1.0, unit: 'tsp/gal', optional: true }
        ]
      },
      strengthMultipliers: {
        light: 0.5,
        medium: 0.75,
        aggressive: 1.0
      }
    },
    'canna': {
      name: 'Canna Coco',
      description: 'Specifically designed for Coco Coir',
      products: {
        vegetative: [
          { name: 'Canna Coco A', ratio: 2.5, unit: 'ml/L' },
          { name: 'Canna Coco B', ratio: 2.5, unit: 'ml/L' }
        ],
        flowering: [
          { name: 'Canna Coco A', ratio: 2.5, unit: 'ml/L' },
          { name: 'Canna Coco B', ratio: 2.5, unit: 'ml/L' }
        ],
        supplements: [
          { name: 'Rhizotonic', ratio: 4.0, unit: 'ml/L', optional: true },
          { name: 'Cannazym', ratio: 2.5, unit: 'ml/L', optional: true }
        ]
      },
      strengthMultipliers: {
        light: 0.6,
        medium: 0.8,
        aggressive: 1.0
      }
    }
  };

  const calculateNutrients = () => {
    const brand = nutrientBrands[selectedBrand];
    const products = brand.products[growthStage];
    const supplements = brand.products.supplements;
    const multiplier = brand.strengthMultipliers[feedingStrength];

    // Convert tank size to appropriate units
    const tankVolume = tankSize; // Already in liters

    const calculations = {
      tankSize: tankSize,
      brand: brand.name,
      stage: growthStage,
      strength: feedingStrength,
      baseNutrients: [],
      supplements: [],
      totalCost: 0,
      instructions: []
    };

    // Calculate base nutrients
    products.forEach(product => {
      let amount = product.ratio * multiplier;

      if (product.unit === 'tsp/gal') {
        // Convert teaspoons per gallon to ml per tank
        // 1 tsp = 4.92892 ml, 1 gallon = 3.78541 L
        amount = (amount * 4.92892 * tankVolume) / 3.78541;
        calculations.baseNutrients.push({
          name: product.name,
          amount: Math.round(amount * 10) / 10,
          unit: 'ml',
          originalRatio: product.ratio,
          originalUnit: product.unit
        });
      } else {
        // ml/L - multiply by tank volume
        amount = amount * tankVolume;
        calculations.baseNutrients.push({
          name: product.name,
          amount: Math.round(amount * 10) / 10,
          unit: 'ml',
          originalRatio: product.ratio,
          originalUnit: product.unit
        });
      }
    });

         // Calculate supplements
     supplements.forEach(supplement => {
       if (supplement.hydroOnly && growMedium !== 'hydro' && growMedium !== 'soilless') return;
       if (supplement.floweringOnly && growthStage !== 'flowering') return;

      let amount = supplement.ratio * multiplier;

      if (supplement.unit === 'tsp/gal') {
        amount = (amount * 4.92892 * tankVolume) / 3.78541;
      } else {
        amount = amount * tankVolume;
      }

      calculations.supplements.push({
        name: supplement.name,
        amount: Math.round(amount * 10) / 10,
        unit: 'ml',
        optional: supplement.optional,
        originalRatio: supplement.ratio,
        originalUnit: supplement.unit
      });
    });

    // Add mixing instructions
    calculations.instructions = [
      "1. Start with clean, pH-adjusted water in your tank",
      "2. Add nutrients in the order listed (important for some brands)",
      "3. Mix thoroughly between each addition",
             "4. Check and adjust pH to 5.5-6.5 for hydro/coco/soilless, 6.0-7.0 for soil",
      "5. Check PPM/EC levels and adjust if needed",
      "6. Use within 7-10 days for best results"
    ];

    if (selectedBrand === 'general-hydroponics') {
      calculations.instructions.splice(1, 1, 
        "2. Add in order: Armor Si first (if using), then CaliMagic, then FloraMicro, FloraGro, FloraBloom"
      );
    }

    setCalculations(calculations);
  };

  const copyToClipboard = () => {
    if (!calculations) return;

    let text = `üß™ Nutrient Recipe - ${calculations.brand}\n`;
    text += `üìä Tank Size: ${calculations.tankSize}L\n`;
    text += `üå± Stage: ${calculations.stage}\n`;
    text += `üí™ Strength: ${calculations.strength}\n\n`;

    text += `Base Nutrients:\n`;
    calculations.baseNutrients.forEach(nutrient => {
      text += `‚Ä¢ ${nutrient.name}: ${nutrient.amount}ml\n`;
    });

    if (calculations.supplements.length > 0) {
      text += `\nSupplements:\n`;
      calculations.supplements.forEach(supplement => {
        text += `‚Ä¢ ${supplement.name}: ${supplement.amount}ml ${supplement.optional ? '(optional)' : ''}\n`;
      });
    }

    text += `\nInstructions:\n`;
    calculations.instructions.forEach(instruction => {
      text += `${instruction}\n`;
    });

    navigator.clipboard.writeText(text);
    setCopiedToClipboard(true);
    toast.success('Recipe copied to clipboard!');
    setTimeout(() => setCopiedToClipboard(false), 3000);
  };

  useEffect(() => {
    calculateNutrients();
  }, [selectedBrand, growthStage, tankSize, waterType, growMedium, feedingStrength]);

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-800">üß™ Cannabis Nutrient Calculator</h1>
          <p className="text-gray-600 mt-1">Calculate precise nutrient mixing ratios for your grow</p>
        </div>
        {calculations && (
          <button
            onClick={copyToClipboard}
            className="btn btn-secondary"
            title="Copy recipe to clipboard"
          >
            {copiedToClipboard ? (
              <CheckCircle className="w-4 h-4 text-green-500" />
            ) : (
              <Copy className="w-4 h-4" />
            )}
            Copy Recipe
          </button>
        )}
      </div>

      {/* Calculator Form */}
      <div className="card">
        <h2 className="section-title">‚öôÔ∏è Calculator Settings</h2>

        <div className="grid grid-3 gap-4">
          <div className="form-group">
            <label className="label">Nutrient Brand</label>
            <select 
              className="select"
              value={selectedBrand}
              onChange={(e) => setSelectedBrand(e.target.value)}
            >
              {Object.entries(nutrientBrands).map(([key, brand]) => (
                <option key={key} value={key}>{brand.name}</option>
              ))}
            </select>
            <p className="text-sm text-gray-500 mt-1">
              {nutrientBrands[selectedBrand].description}
            </p>
          </div>

          <div className="form-group">
            <label className="label">Growth Stage</label>
            <select 
              className="select"
              value={growthStage}
              onChange={(e) => setGrowthStage(e.target.value)}
            >
              <option value="vegetative">üå± Vegetative</option>
              <option value="flowering">üå∏ Flowering</option>
            </select>
          </div>

          <div className="form-group">
            <label className="label">Tank Size (Liters)</label>
            <input
              type="number"
              className="input"
              value={tankSize}
              onChange={(e) => setTankSize(parseFloat(e.target.value) || 50)}
              min="1"
              max="1000"
              step="1"
            />
          </div>

          <div className="form-group">
            <label className="label">Feeding Strength</label>
            <select 
              className="select"
              value={feedingStrength}
              onChange={(e) => setFeedingStrength(e.target.value)}
            >
              <option value="light">üåø Light (50% strength)</option>
              <option value="medium">üå± Medium (75% strength)</option>
              <option value="aggressive">üí™ Aggressive (100% strength)</option>
            </select>
          </div>

          <div className="form-group">
            <label className="label">Grow Medium</label>
            <select 
              className="select"
              value={growMedium}
              onChange={(e) => setGrowMedium(e.target.value)}
            >
              <option value="hydro">üíß Hydroponic/DWC</option>
              <option value="soilless">üåæ Soilless/Peat (ASB WP420, Pro-Mix, etc.)</option>
              <option value="coco">ü•• Coco Coir</option>
              <option value="soil">üåç Soil</option>
            </select>
          </div>

          <div className="form-group">
            <label className="label">Water Type</label>
            <select 
              className="select"
              value={waterType}
              onChange={(e) => setWaterType(e.target.value)}
            >
              <option value="soft">üíß Soft Water (RO/Distilled)</option>
              <option value="hard">ü™® Hard Water (Tap)</option>
            </select>
          </div>
        </div>
      </div>

      {/* Results */}
      {calculations && (
        <div className="space-y-6">
          {/* Recipe Card */}
          <div className="card">
            <h2 className="section-title">üìã Your Nutrient Recipe</h2>

            <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-6">
              <div className="flex items-center gap-2 mb-2">
                <Beaker className="w-5 h-5 text-green-600" />
                <h3 className="font-semibold text-green-800">
                  {calculations.brand} - {calculations.stage} stage
                </h3>
              </div>
              <p className="text-green-700">
                For {calculations.tankSize}L tank at {calculations.strength} strength
              </p>
            </div>

            {/* Base Nutrients */}
            <div className="mb-6">
              <h3 className="text-lg font-semibold mb-3 flex items-center gap-2">
                <TrendingUp className="w-5 h-5 text-blue-500" />
                Base Nutrients (Required)
